   * Navigates to the given url.
   * @param {String} href
   */
  navigate(href) {
    this.location.push(href);
    this.dispatcher.dispatch({
      actionType: 'ROUTER_NAVIGATE',
      href: href
    });
  }

  /**
   * Handle start, change and navigate actions.
   * @param {String} href
   */
